import "core-js/modules/es.array.push.js";
import axios from "axios";
export default {
  name: "AddBook",
  data() {
    return {
      newBook: {
        title: "",
        author: "",
        genre: "",
        summary: ""
      },
      snackbar: false,
      snackbarText: "",
      progressBar: false
    };
  },
  methods: {
    async addBook() {
      try {
        this.progressBar = true;
        const gotAddRes = await axios.post("http://localhost:5000/books/add", this.newBook);
        console.log("checkinggotAddRes", gotAddRes);
        if (gotAddRes) {
          this.snackbarText = gotAddRes.data;
          this.snackbar = true;
        }
        this.progressBar = false;
      } catch (err) {
        console.log("checkingerrr..", err);
        this.snackbarText = err;
        this.snackbar = true;
        this.progressBar = false;
        throw new Error(err);
      }
    },
    goBack() {
      this.$router.push({
        name: "BookList"
      });
    }
  }
};
