      progressBar: false
    };
  },
  methods: {
    addBookCallFn() {
      this.$router.push({
        name: 'AddBook'
      });
    },
    viewBook(id) {
      this.$router.push({
        name: 'BookDetails',
        params: {
          id
        }
      });
    },
    async fetchAllBooks() {
      try {
        this.progressBar = true;
        const gotListRes = await axios.get("http://localhost:5000/books/list");
        console.log("checkinggotListRes", gotListRes);
        this.books = gotListRes.data;
        this.progressBar = false;
      } catch (err) {
        this.progressBar = false;
        console.log("checkingerr...", err);
        throw new Error(err);
      }
    }
  },
  async mounted() {
    await this.fetchAllBooks();
  }
};
