        if (gotAddRes) {
          this.snackbarText = gotAddRes.data;
          this.snackbar = true;
        }
      } catch (err) {
        console.log("checkingerrr..", err);
        this.snackbarText = err;
        this.snackbar = true;
        throw new Error(err);
      }
    },
    deleteBook(index) {
